create table levels (level_id bigint generated by default as identity, name varchar(255), primary key (level_id))
create table levels_persons (level_level_id bigint not null, persons_person_id bigint not null)
create table persons (person_id bigint generated by default as identity, name varchar(40) not null, last_name varchar(40) not null, level_id bigint, role_id bigint, primary key (person_id))
create table projects (project_id bigint generated by default as identity, description varchar(255), name varchar(255) not null, primary key (project_id))
create table projects_person_list (project_project_id bigint not null, person_list_person_id bigint not null)
create table roles (role_id bigint generated by default as identity, name varchar(255) not null, primary key (role_id))
create table teams (team_id bigint generated by default as identity, name varchar(255), primary key (team_id))
